import { useEffect, useState } from "react";
import TableModel from "../../../Models/TableModel";
import dataService from "../../../Services/DataService";
import appConfig from "../../../Utils/AppConfig";
import Table from 'react-bootstrap/Table';
import "./TableList.css";

function TableList(): JSX.Element {

    const [table, setTable] = useState<TableModel[]>([]);
    // const [winner, setWinner] = useState(null);
    const [looser, setLooser] = useState(null);
    const [winner, setWinner] = useState([]);


    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    const handleClick = () => {
        // צור מערך חדש שיכיל את כל האובייקטים
        const allItems = [...table];

        // צור מערך ריק שיכיל את האובייקטים שניצחו
        const winners = [];

        // בצע לולאה על כל האובייקטים במערך
        for (let i = 0; i < allItems.length; i++) {
          // עבור כל אובייקט במערך, מצא את האובייקט הבא במערך שלא היה כבר מתחרה של האובייקט הנוכחי
          let nextItemIndex = i + 1;
          while (nextItemIndex < allItems.length && allItems[i].teamId === allItems[nextItemIndex].teamId) {
            nextItemIndex++;
          }

          // אם האובייקט הנוכחי מנצח את האובייקט הבא, הוסף את האובייקט הנוכחי למערך המנצחים
        //   if (allItems[i].points > allItems[nextItemIndex].points) {
            winners.push(allItems[i]);
        //   }
        }

        // לאחר שהלולאה מסתיימת, החזיר את המערך המנצחים
        setWinner(winners);
      };

    // const handleClick = () => {
    //     // מצא שני אובייקטים אקראיים
    //     const [a, b] = table.sort(() => Math.random() - 0.5).slice(0, 2);

    //     // חשב מי מנצח
    //     if (a.points > b.points) {
    //         b.games +=1
    //         b.loses +=1
    //         a.games += 1
    //         a.points += 3
    //         a.wins +=1;
    //         console.log(a)
    //         console.log(b)
    //         setWinner(a)
    //         setLooser(b)
    //     } else {
    //         a.games +=1;
    //         a.loses +=1;
    //         b.games +=1;
    //         b.points +=3
    //         b.wins +=1;
    //         console.log(b)
    //         console.log(a)
    //         setWinner(b);
    //         setLooser(a)
    //     }
    // };

    return (
        <div className="TableList">
            <button onClick={handleClick}>התחל</button>
            {winner && (
        <p>המנצחים הם: {winner.map((winner) => winner.name)}</p>
      )}

            {/* {winner   &&   (
                <p> {winner.name}: המנצחת היא      <br />
                 {looser.name}: המפסידה היא </p>
            )} */}
            {/* {winner && (
        <p>המנצחים הם: {winner.map((winner) => winner.name)}</p>
      )} */}
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>Place</th>
                        <th>Name</th>
                        <th>Games</th>
                        <th>Wins</th>
                        <th>Loses</th>
                        <th>Draw</th>
                        <th>Points</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {table.sort((a, b) => b.points - a.points).map((t, index) =>
                        <tr key={t.teamId}>
                            <td>{index + 1}</td>
                            <td>{t.name}</td>
                            <td>{t.games}</td>
                            <td>{t.wins}</td>
                            <td>{t.loses}</td>
                            <td>{t.draw}</td>
                            <td>{t.points}</td>
                            <td>
                                <img className="img" src={appConfig.playersImagesUrl + t.imageName} style={{ width: '20px' }} />
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        </div>
    );
}

export default TableList;
