import dataService from "../../../Services/DataService";
import TableModel from "../../../Models/TableModel";
import appConfig from "../../../Utils/AppConfig";
import { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import "./Scoreboard.css";

function Scoreboard(): JSX.Element {

    const [table, setTable] = useState<TableModel[]>([]);

    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    const awayTeams = table.filter(winner => winner.awayTeam).map((winner => winner.name));
    console.log("away team " + awayTeams)
    console.log("---------------------------");
    const homeTeams = table.filter(winner => winner.homeTeam).map((winner => "home team" + winner.name));
    console.log("home team " + homeTeams)

    const randomNumber = Math.floor(Math.random() * 3) + 1;


    return (
        <div className="Scoreboard">
            <div className="tables-container">
                <Table className="table1" >
                    <thead>
                        <tr>
                            <th>קבוצה חוץ</th>
                            <th>תוצאה</th>
                        </tr>
                    </thead>
                    <tbody >
                        {table.filter((winner) => {
                            return winner.homeTeam ;
                        }).map((winner) =>
                            <tr key={winner.teamId}>
                                <td><img className="img" src={appConfig.playersImagesUrl + winner.imageName} style={{ width: '15px' }} />
                                    {winner.name}
                                </td>
                                <td>{winner.goals}</td>
                            </tr>)}
                    </tbody>
                </Table>
                <Table className="table2" >
                    <thead>
                        <tr>
                            <th>תוצאה</th>
                            <th>קבוצה בית</th>
                        </tr>
                    </thead>
                    {table.filter((winner) => {
                        return winner.awayTeam ;
                    }).map((winner) =>
                        <tr key={winner.teamId}>
                            <td>{winner.goals}</td>
                            <td>{winner.name} <img className="img" src={appConfig.playersImagesUrl + winner.imageName} style={{ width: '15px' }} />
                            </td>
                        </tr>)}
                </Table>
            </div>
        </div>
    );
}
export default Scoreboard;
