import dataService from "../../../Services/DataService";
import TableModel from "../../../Models/TableModel";
import { useEffect, useState } from "react";
import "./Scoreboard.css";
import { Table } from "react-bootstrap";
import appConfig from "../../../Utils/AppConfig";

function Scoreboard(): JSX.Element {

    const [table, setTable] = useState<TableModel[]>([]);

    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    return (
        <div className="Scoreboard">
            <h1>scoreboard</h1>
            <div className="tables-container">
                <Table className="table1" >
                    <thead>
                        <tr>
                            <th>קבוצה בית</th>
                            <th>תוצאה</th>
                        </tr>
                    </thead>
                    <tbody >
                        {table.filter((winner) => {
                            return winner.totalWins === 2;
                        }).map((winner) =>
                            <tr key={winner.teamId}>
                                <td><img className="img" src={appConfig.playersImagesUrl + winner.imageName} style={{ width: '15px' }} />
                                {winner.name} 
                                </td>
                                <td>{winner.goals}</td>
                            </tr>
                        )}
                    </tbody>
                </Table>

                <Table className="table2" >
                    <thead>
                        <tr>
                            <th>תוצאה</th>
                            <th>קבוצה חוץ</th>
                        </tr>
                    </thead>
                    {table.filter((winner) => {
                        return winner.totalWins === 1;
                    }).map((winner) =>
                        <tr key={winner.teamId}>
                            <td>{winner.goals}</td>
                            <td>{winner.name} <img className="img" src={appConfig.playersImagesUrl + winner.imageName} style={{ width: '15px' }} />
                            </td>
                        </tr>
                    )}
                </Table>
            </div>
        </div>

    );
}
export default Scoreboard;
