import { useState, useEffect, ChangeEvent } from "react";
import TaskModel from "../../../Models/TaskModel";
import dataService from "../../../Services/DataService";
import DataCard from "../DataCard/DataCard";
import { tasksStore } from "../../../Redux/taskState";
import Pagination from "../Pagination/Pagination";
import "./DataList.css";
import kindTask from "../../../Models/KindTaskModel";
import notify from "../../../Utils/Notify";

function DataList(): JSX.Element {

    const [tasks, setTask] = useState<TaskModel[]>([]);
    const [kindTasks, setKindTask] = useState<kindTask[]>([]);
    const [text, setText] = useState<string>('');
    const [currentPage, setCurrentPage] = useState(1);
    const [postPerPage] = useState(3);

    // useEffect(() => {
    //     dataService.getAllTasks2()
    //         .then(task => setTask(task))
    //         .catch(err => console.log(err))
    //     tasksStore.subscribe(() => {
    //         const dup = [...tasksStore.getState().tasks.filter((task: TaskModel) => (!task.isArchived && !dataService.isMoreThanWeekFromNow(task)))];
    //         setTask(dup);
    //     })
    // }, [])

    useEffect(() =>{
        dataService.getAllTasksByKindTask()
        .then(task => setKindTask(task))
        .catch(err => console.log(err))
    },[])

    async function name(args: ChangeEvent<HTMLSelectElement>) {
        try {
            const kindTaskId = +args.target.value;
            const dbTheater = await dataService.getTaskByKindTask(kindTaskId)
            setTask(dbTheater)
        }
        catch (err) {
            console.log(err)
        }
    }

    function isFollowing(isFollowing: number) {
        return isFollowing ? dataService.getAllTask : dataService.getAllTasks2;
    }

    function getAllTasks() {
        dataService.getAllTask()
            .then(task => setTask(task))
            .catch(err => console.log(err))
    }

    function getAllTasks2() {
        dataService.getAllTasks2()
            .then(task => setTask(task))
            .catch(err => console.log(err))
    }

    function getTasksOfToday() {
        dataService.getTasksOfToday()
            .then(task => setTask(task))
            .catch(err => console.log(err))
    }

    function getTasksOfTomorrow() {
        dataService.getTasksOfTomorrow()
            .then(task => setTask(task))
            .catch(err => console.log(err))
    }

    async function deleteClickedTask(taskId: number) {
        await dataService.deleteTask(taskId);
        const duplicatedMovies = [...tasks];
        const index = duplicatedMovies.findIndex(t => t.taskId === taskId);
        duplicatedMovies.splice(index, 1);
        setTask(duplicatedMovies);
    }

    const search = () => {
        const findTasks =
            tasks && tasks?.length > 0 ? tasks?.filter((s) => s?.name === text) : undefined
        setTask(findTasks)
    }

    const indexOfLastPost = currentPage * postPerPage;
    const indexOfFirstPost = indexOfLastPost - postPerPage;
    const currentPost = tasks.slice(indexOfFirstPost, indexOfLastPost);

    return (
        <div className="DataList box">
            <div className="row row-cols-1 row-cols-md-6 g-4 wrapper">
                <div className="input_wrapper">
                    <button onClick={getTasksOfTomorrow}>Tomorrow</button>
                    <span> | </span>
                    <button onClick={getTasksOfToday}>Today</button>
                    <span>  </span>
                    <input type="text" placeholder="search task" value={text}
                        onChange={(e) => {
                            setText(e.target.value);
                        }} />
                    <button disabled={!text} onClick={search}>search</button>
                    {tasks && tasks?.length === 0 && (
                        <div className="notFound">The task is not found</div>
                    )}
                </div>
                <br />
                <select defaultValue="" onChange={name}>
                    {<option disabled value="">select kind task</option>}
                    {kindTasks.map(t => <option key={t.kindTaskId} value={t.kindTaskId}>{t.taskName}</option>)}
                </select>

                {!isFollowing(1) ? <button className="btn btn-outline-primary"
                    onClick={getAllTasks}>
                </button> : <button onClick={getAllTasks2}>Toggle Tasks</button>}
                <br />
                {currentPost.map((item) => <DataCard key={item.taskId} task={item} deleteMe={deleteClickedTask} />)}
                <br />
                {<Pagination totalPost={tasks.length} postPerPage={postPerPage} currentPage={currentPage} setCurrentPage={setCurrentPage} />}
            </div>
        </div>
    );
}
export default DataList
