import { createStore } from "redux";
import TeamModel from "../Models/TeamModel";
import CountryModel from "../Models/CountryModel";
import TableModel from "../Models/TableModel";


export class PlayersState {
    players: TeamModel[] = [];
    table: TableModel[] = [];
    kindCountry: CountryModel[] = [];
}

export enum PlayersActionType {
    FetchKindCountries = "FetchKindCountries",
    FetchPlayers = "FetchPlayers",
    FetchTopPlayers = "FetchTopPlayers",
    FetchTable = "FetchTable",
    AddPlayer = "AddPlayer",
    DeletePlayer = "DeletePlayer",
    UpdatePlayer = "UpdatePlayer",
}

export interface PlayersAction {
    type: PlayersActionType;
    payload: any
}

export function playersReducer(currentState = new PlayersState(), action: PlayersAction): PlayersState {
    const newState = { ...currentState };

    switch (action.type) {
        case PlayersActionType.FetchPlayers:
            newState.players = action.payload;
            break;

        case PlayersActionType.FetchTopPlayers:
            const getTopPlayers = (state) => state.topPlayers;

            if (action.payload.length > 0) {
                const topPlayers = action.payload.slice(0, 5);
                return {
                    ...currentState,
                    players: topPlayers,
                };
            }
            break;

        case PlayersActionType.FetchTable:
            newState.table = action.payload;
            break;

        case PlayersActionType.FetchKindCountries:
            newState.kindCountry = action.payload;
            break;

        case PlayersActionType.AddPlayer:
            if (newState.players.length > 0) {
                newState.players.push(action.payload)
            }
            break;

        case PlayersActionType.DeletePlayer:
            const indexToDelete = newState.players.findIndex(t => t.playerId === action.payload)
            if (indexToDelete >= 0) {
                newState.players.splice(indexToDelete, 1)
            }
            break;

        case PlayersActionType.UpdatePlayer:
            const indexToUpdate = newState.players.findIndex(t => t.playerId === action.payload.taskId)
            if (indexToUpdate >= 0) {
                newState.players[indexToUpdate] = action.payload;
            }
            break;
    }
    return newState;
}
export const playersStore = createStore(playersReducer)
