import axios from "axios";
import appConfig from "../Utils/AppConfig";
import TeamModel from "../Models/TeamModel";
import CountryModel from "../Models/CountryModel";
import TableModel from "../Models/TableModel";
import { PlayersActionType, playersStore } from "../Redux/taskState";

class DataService {

    async getAllPlayers(): Promise<TeamModel[]> {
        try {
            let players = playersStore.getState().players;
            if (players.length === 0) {
                const response = await axios.get<TeamModel[]>(appConfig.playersUrl);
                players = response.data;
                playersStore.dispatch({ type: PlayersActionType.FetchPlayers, payload: players })
            }
            return players;
        } catch (err) {
            console.log(err)
        }
    }

    async getTopGoalScorers(): Promise<TeamModel[]> {
        try {
            let players = playersStore.getState().topPlayers;
            if (players.length === 0) {
                const response = await axios.get<TeamModel[]>(appConfig.topPlayersUrl);
                players = response.data;
                playersStore.dispatch({ type: PlayersActionType.FetchTopPlayers, payload: players })
            }
            return players;
        } catch (err) {
            console.log(err)
        }
    }

    async getTheTable(): Promise<TableModel[]> {
        const [players, setPlayers] = useState<TeamModel[]>([]);

        try {
            let players = playersStore.getState().topPlayers;
            let table = playersStore.getState().table;
            if (table.length === 0) {
                const response = await axios.get<TableModel[]>(appConfig.tableUrl);
                table = response.data;
                playersStore.dispatch({ type: PlayersActionType.FetchTable, payload: table })
                playersStore.dispatch({ type: PlayersActionType.FetchTopPlayers, payload: players })
              
                const currentObject1 = players[Math.floor(Math.random() * players.length)];
                const currentObject2 = players[Math.floor(Math.random() *  players.length)];
        
                const scoredPlayer1 = currentObject1;
                const scoredPlayer2 = currentObject2;
        
                console.log(scoredPlayer1.goals += 1 , scoredPlayer1.lastName);
                console.log(scoredPlayer2.goals += 1 , scoredPlayer2.lastName);
                this.getTopGoalScorers();
            }
            return table;
        } catch (err) {
            console.log(err)
        }
    }

    async getAllTasksByKindTask(): Promise<CountryModel[]> {
        try {
            let kindCountry = playersStore.getState().kindCountry;
            const response = await axios.get<CountryModel[]>(appConfig.countriesUrl);
            kindCountry = response.data;
            return kindCountry;
        } catch (err) {
            console.log(err)
        }
    }

    async getTaskByKindTask(kindTaskId: number): Promise<TeamModel[]> {
        let kindTask = playersStore.getState().players
        let task = kindTask.find(t => t.playerId === kindTaskId)
        if (!task) {
            const response = await axios.get<TeamModel[]>(appConfig.taskByTaskUrl + kindTaskId);
            kindTask = response.data;
        }
        return kindTask;
    }


    async addTask(task: TeamModel): Promise<void> {
        try {
            const headers = { "Content-Type": "multipart/form-data" };
            const response = await axios.post<TeamModel>(appConfig.playersUrl, task, { headers })
            const addedTask = response.data;
            playersStore.dispatch({ type: PlayersActionType.AddPlayer, payload: addedTask })
        } catch (err) {
            console.log(err)
        }
    }

    async deleteTask(taskId: number): Promise<void> {
        try {
            await axios.delete(appConfig.playersUrl + taskId);
            playersStore.dispatch({ type: PlayersActionType.DeletePlayer, payload: taskId })
        } catch (err) {
            console.log(err);
        }
    }

    async updateTask(task: TeamModel): Promise<void> {
        try {
            const headers = { "Content-Type": "multipart/form-data" };
            const response = await axios.put<TeamModel>(appConfig.playersUrl + task.playerId, task, { headers });
            const updateTask = response.data;
            console.log(updateTask);
            playersStore.dispatch({ type: PlayersActionType.UpdatePlayer, payload: updateTask });
        } catch (err) {
            console.log(err)
        }
    }
}
const dataService = new DataService();
export default dataService;
