import { useEffect, useState } from "react";
import "./Statistics.css";
import TeamModel from "../../../Models/TeamModel";
import { Table } from "react-bootstrap";
import appConfig from "../../../Utils/AppConfig";
import dataService from "../../../Services/DataService";

const Statistics = () => {
    const [players, setPlayers] = useState<TeamModel[]>([]);

    useEffect(() => {
        dataService.getTopGoalScorers()
            .then(table => setPlayers(table))
            .catch(err => console.log(err));
    }, []);


    // const onClick = () => {
    //     // מקבל את השחקן הנוכחי
    //     const currentObject1 = players[Math.floor(Math.random() * players.length)];
    //     const currentObject2 = players[Math.floor(Math.random() * players.length)];

    //     const scoredPlayer1 = currentObject1;
    //     const scoredPlayer2 = currentObject2;

    //     console.log(scoredPlayer1.goals += 1, scoredPlayer1.lastName);
    //     console.log(scoredPlayer2.goals += 1, scoredPlayer2.lastName);
    //     setPlayers(players)
    // };

    return (
        <div className="Statistics">
            <h1>statistics
            </h1>
            {/* <button onClick={onClick}>מחשב מספר שערים</button> */}
            <Table striped bordered hover >
                <thead>
                    <h1>TOP GOALSCORERS</h1>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Goals</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {players.sort((a, b) => b.goals - a.goals).map((t, index) =>
                        <tr key={t.playerId}>
                            {/* <td>{index + 1}</td> */}
                            <td>{t.firstName}</td>
                            <td>{t.lastName}</td>
                            <td>{t.goals}</td>
                            <td>
                                <img className="img" src={appConfig.playersImagesUrl + t.imageName} style={{ width: '50px' }} />
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        </div>
    );
}

export default Statistics;
