import { useState } from "react";
import "./Statistics.css";
import TeamModel from "../../../Models/TeamModel";

function Statistics(): JSX.Element {

    const [players, setPlayers] = useState<TeamModel[]>([]);

    function getGoalChance(team: TeamModel) {
        // const allItems = [...players];

        // allItems.sort(() => Math.random() - 0.5);

        for (let i = 0; i < players.length; i++) {
            // מחזיר את הסיכוי לכיבוש שער בהתאם למיקום השחקן
            if (team.role === "striker" || team.role === "left winger" || team.role === "right winger") {
                return 0.5;
            } else {
                return 0.25;
            }
        }
    }
    function onClick() {
        // מקבל את השחקן הנוכחי
        const currentObject = players[Math.floor(Math.random() * players.length)];

        // מחשב את מספר השערים שכבשו
        const numberOfGoals = Math.floor(Math.random() * getGoalChance(currentObject));

        // מעדכן את מספר השערים אם השחקן כבש שער
        if (numberOfGoals > 0) {
            currentObject.goals += numberOfGoals;
        }

        // מעדכן את הסטטוס של השחקן
        currentObject.scored = numberOfGoals > 0;
    }

    return (
        <div className="Statistics">
            <h1>statistics
            </h1>
            <button onClick={onClick}>מחשב מספר שערים</button>

        </div>
    );
}

export default Statistics;
