import { useState, useEffect } from "react";
import dataService from "../../../Services/DataService";
import DataCard from "../DataCard/DataCard";
import TeamModel from "../../../Models/TeamModel";
import "./DataList.css";
import { playersStore } from "../../../Redux/taskState";
import TableModel from "../../../Models/TableModel";

function DataList(): JSX.Element {

    const [players, setPlayers] = useState<TeamModel[]>([]);
    const [table, setTable] = useState<TableModel[]>([]);
    const [text, setText] = useState<string>('');

    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    useEffect(() => {
        dataService.getAllPlayers()
            .then(task => setPlayers(task))
            .catch(err => console.log(err))
        playersStore.subscribe(() => {
            const dup = [...playersStore.getState().players.filter((task: TeamModel) => (task))];
            setPlayers(dup);
        })
    }, [])

    const totalPrice = players.reduce((acc, player) => acc + player.price, 0);
    const averageAge = players.reduce((acc, player) => acc + player.age, 0) / players.length;

    function getPositionByName(table: TableModel[], name: "Manchester United"): number {
        for (let i = 0; i < table.length; i++) {
          if (table[i].name === name) {
            return i;
          }
        }
        return -1; // לא נמצאה קבוצה עם השם המבוקש
      }

      // const position = table.filter(table => table.teamId === 6);
      const position = table.indexOf("Manchester United");

    console.log(position)


    async function deleteClickedTask(taskId: number) {
        await dataService.deleteTask(taskId);
        const duplicatedMovies = [...players];
        const index = duplicatedMovies.findIndex(t => t.playerId === taskId);
        duplicatedMovies.splice(index, 1);
        setPlayers(duplicatedMovies);
    }

    const search = () => {
        const findTasks =
            players && players?.length > 0 ? players?.filter((s) => s?.firstName + " " + s?.lastName === text) : undefined
        setPlayers(findTasks)
    }

    return (
        <div className="DataList box">
            <div className="row row-cols-1 row-cols-md-6 g-4 wrapper">

                <div className="input_wrapper">
                    <input type="text" placeholder="search task" value={text}
                        onChange={(e) => { setText(e.target.value); }} />
                    <button disabled={!text} onClick={search}>search</button>
                    {players && players?.length === 0 && (
                        <div className="notFound">The task is not found</div>
                    )}
                </div>
                <h3 style={{ color: 'blue' }} >Total Market Value = ${totalPrice.toFixed()}M</h3>
                <h3 style={{ color: 'blue' }} >Average Age = {averageAge.toFixed()}</h3>
                <h3 style={{ color: 'blue' }} >Table Position = {position.toString()}</h3>
                <br />
                <button onClick={() =>getPositionByName}>getPositionByName</button>
                {players.map((item) => <DataCard key={item.playerId} player={item} deleteMe={deleteClickedTask} />)}
                <br />
            </div>
        </div>
    );
}
export default DataList;
