import { useEffect, useState } from "react";
import TableModel from "../../../Models/TableModel";
import dataService from "../../../Services/DataService";
import appConfig from "../../../Utils/AppConfig";
import Table from 'react-bootstrap/Table';
import "./TableList.css";
import TeamModel from "../../../Models/TeamModel";

function TableList(): JSX.Element {

    const [players, setPlayers] = useState<TeamModel[]>([]);
    const [table, setTable] = useState<TableModel[]>([]);
    const [winners, setWinners] = useState([]);
    const [losers, setLosers] = useState([]);

    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    useEffect(() => {
        dataService.getTopGoalScorers()
            .then(table => setPlayers(table))
            .catch(err => console.log(err));
    }, []);

    useEffect(() => {
        dataService.getTopAssists()
            .then(table => setPlayers(table))
            .catch(err => console.log(err));
    }, []);

    const handleClick = () => {
        // צור מערך חדש שיכיל את כל האובייקטים
        const allItems = [...table];

        // ערבב את האובייקטים במערך
        allItems.sort(() => Math.random() - 0.5);

        // צור מערך ריק שיכיל את האובייקטים שניצחו
        let winners = [];

        let losers = [];

        // בצע לולאה על כל האובייקטים במערך
        for (let i = 0; i < allItems.length; i++) {
            // בדוק אם האובייקט כבר ניצח
            const alreadyWon = winners.some((winner) => winner.teamId === allItems[i].teamId);
            const alreadyLost = losers.some((loser) => loser.teamId === allItems[i].teamId);

            // אם לא, הוסף אותו למערך המנצחים
            if (!alreadyWon && !alreadyLost) {
                if (i < 10 && allItems[i].games <= 37) {
                    winners.push(allItems[i]);
                } else if (allItems[i].games <= 37) {
                    losers.push(allItems[i]);
                }
            }
        }

        // לאחר שהלולאה מסתיימת, עדכן את מספר הנקודות של כל אובייקט במערך המנצחים ל-3
        for (const winner of winners) {
            if (winner.games <= 37) {
                winner.points += 3;
                winner.wins += 1;
            }
        }

        for (const loser of losers) {
            if (loser.games <= 37) {
                loser.loses += 1;
            }
        }

        for (const game of table) {
            if (game.games <= 37) {
                game.games += 1;
            } else {
                console.log("the league is finished")
            }
        }
        setWinners(winners);
        setLosers(losers);

        // מקבל את השחקן הנוכחי
        const scoredPlayer1 = players[Math.floor(Math.random() * players.length)];
        const scoredPlayer2 = players[Math.floor(Math.random() * players.length)];
        const assistPlayer1 = players[Math.floor(Math.random() * players.length)];
        const assistPlayer2 = players[Math.floor(Math.random() * players.length)];

        let actionExecuted = false;
        for (let i = 18; i < allItems.length; i++) {
            if (allItems[i].games <= 37 && !actionExecuted) {
                scoredPlayer1.goals += 1;
                scoredPlayer2.goals += 1;
                assistPlayer1.assists += 1;
                assistPlayer2.assists += 1;
                actionExecuted = true;
            }
        }
        const getChampion = (table: TableModel) => {
            return table.reduce((acc: { points: number; }, team: { points: number; }) => {
              if (acc.points < team.points) {
                acc = team;
              }
              return acc;
            }, { points: 0 });
          };

          const champion = (table[0]);

          console.log(champion.points); // 30

        // for (let i = 18; i < allItems.length; i++) {
        //     if (allItems[i].games <= 37) {
        //         table.sort((a, b) => b.points - a.points);
        //     } else if (allItems[i].games === 38) {
        //         const champion = table[0];
        //         console.log(champion.name); // "קבוצה 1"
        //     }
        // }
        setPlayers(players)
    };
    // const position = table.filter((table => table.points));

    return (
        <div className="TableList">
            <button onClick={handleClick}>התחל</button>
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>Name</th>
                    </tr>
                </thead>
                <tbody >
                    <tr>המנצחות הן: {winners.map((winner) => <td>{winner.name}</td>)}
                    </tr>
                </tbody>
                <tbody >
                    <tr>המפסידות הן: {losers.map((loser) => <td>{loser.name}</td>)}
                    </tr>
                </tbody>
            </Table>

            <h3 style={{ color: 'blue' }} >Total Market Value = ${totalPrice.toFixed()}M</h3>


            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>Place</th>
                        <th>Name</th>
                        <th>Games</th>
                        <th>Wins</th>
                        <th>Loses</th>
                        <th>Draw</th>
                        <th>Points</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {table.sort((a, b) => b.points - a.points).map((t, index) =>
                        <tr key={t.teamId}>
                            <td>{index + 1}</td>
                            <td>{t.name}</td>
                            <td>{t.games}</td>
                            <td>{t.wins}</td>
                            <td>{t.loses}</td>
                            <td>{t.draw}</td>
                            <td>{t.points}</td>
                            <td>
                                <img className="img" src={appConfig.playersImagesUrl + t.imageName} style={{ width: '20px' }} />
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        </div>
    );
}

export default TableList;
