import React from 'react';
import './SoccerTeamComposition.css'; // Import your CSS file

interface Player {
    role
  name?: string; // Optional player name
  rating?: number; // Optional overall player rating
  image?: string; // Optional player image URL
}

const SoccerTeamComposition: React.FC<CompositionProps> = ({ formation, players }) => {
  // Define formation layout based on props (example for 4-4-2)
  const formationLayout: React.CSSProperties = {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gridTemplateRows: 'repeat(5, 1fr)',
  };

  return (
    <div className="soccer-team-composition" style={formationLayout}>
      {players.map((player) => (
        <div key={player.position} className={`player ${player.position}`}>
          <span className="position">{player.position}</span>
          {/* Optionally display additional player details */}
          {player.name && <span className="name">{player.name}</span>}
          {player.rating && <span className="rating">{player.rating}</span>}
          {player.image && <img src={player.image} alt={player.name} />}
        </div>
      ))}
    </div>
  );
};

interface CompositionProps {
  formation: string;
  players: Player[];
}

export default SoccerTeamComposition;
