import dataService from "../../../Services/DataService";
import TableModel from "../../../Models/TableModel";
import { useEffect, useState } from "react";
import "./Scoreboard.css";
import { Table } from "react-bootstrap";
import appConfig from "../../../Utils/AppConfig";

function Scoreboard(): JSX.Element {

    const [table, setTable] = useState<TableModel[]>([]);

    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])

    return (
        <div className="Scoreboard">
            <h1>scoreboard</h1>
            <Table >
                <tr>
                    <th>קבוצה בית</th>
                    <th>תוצאה</th>
                    <th>קבוצה חוץ</th>
                </tr>
                <tbody >
                    <tr>המנצחות הן: {
                        table.filter((winner) => {
                            return winner.totalWins === 2;
                        }).map((winner) => <td>{winner.name} <td></td> <br />{winner.goals}  <td>
                            <img className="img" src={appConfig.playersImagesUrl + winner.imageName} style={{ width: '20px' }} />
                        </td> </td>)
                    }
                    </tr>
                </tbody>
                <tbody >
                    <tr>המפסידות הן: {
                        table.filter((looser) => {
                            return looser.totalWins === 1;
                        }).map((looser) => <td>{looser.name} <br /> {looser.goals}   <td>
                            <img className="img" src={appConfig.playersImagesUrl + looser.imageName} style={{ width: '20px' }} />
                        </td></td>)
                    }
                    </tr>
                </tbody>
            </Table>
        </div>
    );
}
export default Scoreboard;
