import { useEffect, useState } from "react";
import TableModel from "../../../Models/TableModel";
import "./TableList.css";
import dataService from "../../../Services/DataService";
import appConfig from "../../../Utils/AppConfig";
import Table from 'react-bootstrap/Table';

function TableList(): JSX.Element {

    const [table, setTable] = useState<TableModel[]>([]);
    const [winner, setWinner] = useState(null);
    const [looser, setLooser] = useState(null);


    useEffect(() => {
        dataService.getTheTable()
            .then(table => setTable(table))
            .catch(err => console.log(err))
    }, [])


    const handleClick = () => {
        // מצא שני אובייקטים אקראיים
        const [a, b] = table.sort(() => Math.random() - 0.5).slice(0, 2);

        // חשב מי מנצח
        if (a.points > b.points) {
            a.games += 1
            a.points += 3
            b.games +=1
            console.log(a)
            console.log(b)
            setWinner(a);
            setLooser(b)
        } else {
            b.games +=1;
            b.points +=3
            a.games +=1;
            console.log(b)
            console.log(a)
            setWinner(b);
            setLooser(a)
        }
    };

    return (
        <div className="TableList">
            <button onClick={handleClick}>התחל</button>
            {winner  || looser &&   (
                <p>המנצחת היא {winner.name} && br המפסידה היא {looser.name} </p>
                // <p>המפסידה היא {looser.name}</p>

            )}
            <Table striped bordered hover >
                <thead>
                    <tr>
                        <th>Place</th>
                        <th>Name</th>
                        <th>Games</th>
                        <th>Wins</th>
                        <th>Loses</th>
                        <th>Draw</th>
                        <th>Points</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {table.sort((a, b) => b.points - a.points).map((t, index) =>
                        <tr key={t.teamId}>
                            <td>{index + 1}</td>
                            <td>{t.name}</td>
                            <td>{t.games}</td>
                            <td>{t.wins}</td>
                            <td>{t.loses}</td>
                            <td>{t.draw}</td>
                            <td>{t.points}</td>
                            <td>
                                <img className="img" src={appConfig.playersImagesUrl + t.imageName} style={{ width: '20px' }} />
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        </div>
    );
}

export default TableList;
