import { createStore } from "redux";
import TeamModel from "../Models/TeamModel";
import CountryModel from "../Models/CountryModel";
import TableModel from "../Models/TableModel";


export class TaskState {
    players: TeamModel[] = [];
    table: TableModel[] = [];
    kindTask: CountryModel[] = [];
}

export enum TaskActionType {
    FetchKindCountries = "FetchKindCountries",
    FetchPlayers = "FetchPlayers",
    AddPlayer = "AddPlayer",
    DeletePlayer = "DeletePlayer",
    UpdatePlayer = "UpdatePlayer",
}

export interface TasksAction {
    type: TaskActionType;
    payload: any
}

export function taskReducer(currentState = new TaskState(), action: TasksAction): TaskState {
    const newState = { ...currentState };

    switch (action.type) {
        case TaskActionType.FetchPlayers:
            newState.tasks = action.payload;
            break;

        case TaskActionType.FetchKindCountries:
            newState.kindTask = action.payload;
            break;

        case TaskActionType.AddTask:
            if (newState.tasks.length > 0) {
                newState.tasks.push(action.payload)
            }
            break;

        case TaskActionType.DeleteTask:
            const indexToDelete = newState.tasks.findIndex(t => t.playerId === action.payload)
            if (indexToDelete >= 0) {
                newState.tasks.splice(indexToDelete, 1)
            }
            break;

        case TaskActionType.UpdateTask:
            const indexToUpdate = newState.tasks.findIndex(t => t.playerId === action.payload.taskId)
            if (indexToUpdate >= 0) {
                newState.tasks[indexToUpdate] = action.payload;
            }
            break;
    }
    return newState;
}
export const tasksStore = createStore(taskReducer)
