import { createStore } from "redux";
import TeamModel from "../Models/TeamModel";
import CountryModel from "../Models/CountryModel";
import TableModel from "../Models/TableModel";


export class PlayersState {
    players: TeamModel[] = [];
    table: TableModel[] = [];
    kindCountry: CountryModel[] = [];
}

export enum pActionType {
    FetchKindCountries = "FetchKindCountries",
    FetchPlayers = "FetchPlayers",
    AddPlayer = "AddPlayer",
    DeletePlayer = "DeletePlayer",
    UpdatePlayer = "UpdatePlayer",
}

export interface TasksAction {
    type: TaskActionType;
    payload: any
}

export function taskReducer(currentState = new PlayersState(), action: TasksAction): PlayersState {
    const newState = { ...currentState };

    switch (action.type) {
        case TaskActionType.FetchPlayers:
            newState.players = action.payload;
            break;

        case TaskActionType.FetchKindCountries:
            newState.kindCountry = action.payload;
            break;

        case TaskActionType.AddPlayer:
            if (newState.players.length > 0) {
                newState.players.push(action.payload)
            }
            break;

        case TaskActionType.DeletePlayer:
            const indexToDelete = newState.players.findIndex(t => t.playerId === action.payload)
            if (indexToDelete >= 0) {
                newState.players.splice(indexToDelete, 1)
            }
            break;

        case TaskActionType.UpdatePlayer:
            const indexToUpdate = newState.players.findIndex(t => t.playerId === action.payload.taskId)
            if (indexToUpdate >= 0) {
                newState.players[indexToUpdate] = action.payload;
            }
            break;
    }
    return newState;
}
export const tasksStore = createStore(taskReducer)
